import axios from "axios";
import React , {useState, useEffect} from "react";
import parse from 'html-react-parser';
/*
 * Necessary imports for pdfMake to play nice when changing fonts.
 */
import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts.js";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

/* Standard font definition. ttf files must be added to the backbone of pdfMake first.
 * Only Times New Roman exists in this current build. Any changes will break pdfCreate.
 */
pdfMake.fonts = {
  Times: {
    normal: 'times.ttf',
    bold: 'timesbd.ttf',
    italics: 'timesi.ttf',
    bolditalics: 'timesbi.ttf'
  }
};

const len_words = 50;

export default class ItemDetails extends React.Component {

  /* default State object */
  state = {
    is_ready: false,
    id: "",
    url: "",
    file_urls: "",
    title:"",
    tags:"",
    description:[],
    itemlink:"",
    pdfheader: "",
    pdffooter: ""
  };

  componentDidMount() {
    axios
      .get(this.props.data.values.url)
      .then(res => {
      this.setState({
        is_ready: true,
        pdfheader: this.props.data.values.pdfheader,
        pdffooter: this.props.data.values.pdffooter,
        id: res.data.id,
        url: res.data.url,
        file_urls: res.data.files.url,
        title: res.data.title,
        tags: getTags(res.data.tags),
        description:res.data.element_texts
      });
        
      })
      .catch(error => console.log(error));
  }

  render() {
    return (
      <table className="my-2 ml-2">
        <tbody className="mx-2" id={this.state.id}>
          {this.state.description.map(
            c =>(
              <tr key={c.id + c.text + Math.random() + " $key"}>
                <td className="border-none font-bold">{c.element.name}: </td>
                <td  className="border-none">{parse(c.text)}</td>
              </tr>
            )
          )}

          <tr>
            <td className="border-none font-bold underline">
              Tags: 
            </td>
            <td className="border-none">         
              {this.state.tags}
            </td>
            </tr>
         
          <tr>
            <td className="font-bold border-none align-top">
              File: 
            </td>
            <td className="border-none italic">         
              {this.state.is_ready ? (<ShowFiles data={this.state} /> ): ""}
            </td>
          </tr>
        </tbody>
  
      </table>
    );
  }
}

/* parse the tags array */
function getTags(tagArray) {
  const length = Object.keys(tagArray).length;
  let tags = "";
  for(var i=0; i < length; i++){
    tags += tagArray[i].name;
    if (i < length - 1)
      tags  += ", "
  }
  return tags;
}

/* 
1. list the file links with the original file name
2. show images if available 
note: a url for file api is needed.   */
const ShowFiles = (data) => {

  const [posts, setPosts] = useState([
    {
      id: null,
      url: null,
      description: null,
      filename: null,
      mimetype: null,
      pdfheader: null,
      pdffooter: null,
    }
  ]
  );
   
  useEffect( () => { 
      async function fetchData() {
          try {
              const res = await axios.get(data.data.file_urls); 
              res.data.map(           
                c=>{
                  var newpost = {
                    id: data.data.id,
                    url:c.file_urls.original,
                    description: data.data.description,
                    filename: c.original_filename,
                    mimetype: c.mime_type,
                    pdfheader: data.data.pdfheader,
                    pdffooter: data.data.pdffooter,
                  }
                  setPosts(oldArray => [...oldArray, newpost]);
                  return c;
                }               
              )
          } catch (err) {
              console.log(err);
          }
      }
      fetchData();
  }, [data.data]);

  return (
    <div> 
      <div>{posts.map(entry =>
        entry.url !== null ? (
          <li className="pt-0 list-none hover:list-disc" key={entry.id + entry.url}>
          <a href={entry.url}>{entry.filename}</a> 
          </li>) :""
          )}       
      </div>

      <div>{posts.map(entry =>   
           entry.mimetype === "image/jpeg" ||  entry.mimetype === "image/png" ?
          (<img src={entry.url} alt={entry.filename} className="h-40 inline-block mr-2"/>) : ""
          )        
        }
      </div>

      <div>
        <button className="bg-slate-400 h-10 w-52 inline-block mr-2 hover:bg-sky-500 text-left pl-2"
                    // onClick={() => pdfmakedownload(posts[1].description, posts[1].pdfheader, posts[1].pdffooter)} type="primary">
                    onClick={() => pdfmakedownload(posts)} type="primary">
                Download Front-page PDF</button>
      </div>
      <div>
        {
          pdfexiting(posts) ?
           (<button className="bg-slate-400 h-10 w-52 inline-block mr-2 hover:bg-sky-500 text-left pl-2"
                     onClick={() => pdfmakemerge(posts)} type="primary">
                 Download Merged PDF</button>)
                 :
                 ""     
        }
        
      </div>
      

    </div> 

    );
}

const pdfexiting = (posts) => {
  var x = false;
  let lpost=posts.length;
  for(let i=1; i<lpost; i++){
    if (posts[i].mimetype === "application/pdf"){
      x=true;
    }
  }
    
  return x;

}

/* make the pdf file and hence download it */
const pdfmakedownload = (posts) => {

  let [dd, title] = pdfdata(posts);

  pdfMake.createPdf(dd).download("front_page_"+title+".pdf");

};


/* merge the pdf file with a generated front-page, and hence download it */
const pdfmakemerge = (posts) => {

  let [dd, title] = pdfdata(posts);
  const pdfDocGenerator = pdfMake.createPdf(dd);
  
  const PDFMerger = require('pdf-merger-js');

  var merger = new PDFMerger();
  // console.log(posts);

  pdfDocGenerator.getDataUrl((dataUrl) => {
    (async () => {
      merger.add(dataUrl);
      // await merger.add(posts[1].url);  //merge all pages. parameter is the path to file and filename.
      let lpost = posts.length;
      for(let i=1; i<lpost; i++){
        if (posts[i].mimetype === "application/pdf"){
          await merger.add(posts[i].url); }
      }
      await merger.save("merged_"+title+".pdf"); //save under given name and reset the internal document
    })();
  });

};


const pdfdata=(posts)=>{

  // var solid_line = {canvas: [ { type: 'line', x1: 0, y1: 0, x2: 515, y2: 0, lineWidth: 1 } ],margin: [0, 2, 0, 5] };
  let text = posts[1].description;
  let pdfheader =  posts[1].pdfheader;
  let pdffooter =  posts[1].pdffooter;
 
  var dd = {
    /* the content of the the pdf */
    pageSize: 'LETTER',
    header: "",
    footer: {
      columns: [
        pdffooter,  
      ],alignment: "center",
      style: "small"
    },
    content:[
      {
        columns: [
          {
            text:  "\n\n\n"+pdfheader,
            style: "brand",
          },
          {
            image: 'data:image/jpeg;base64, ',
            fit: [85, 85],
            alignment: 'right',
          }
        ]
      },
      {canvas: [ 
      
        { type: 'line', x1: 0, y1: 0, x2: 495, y2: 0, lineWidth: 1 },],margin: [0, 20, 0, 20] }
      
    ],
    /* Keeps default style Times New Roman. 
     * For your sanity, refrain from changing this if not necessary.
     */
    defaultStyle: {
      font: "Times"
    },

    /* Font styles for each type of font.  
     * Used for pdfmaker. (Like CSS)
     */
    styles: {
      brand: { // repping the George Eliot Scholars brand.
        font: 'Times',
        fontSize: 18,
        color: "#6fac46",
      },
      title:{ // document title font
        font: 'Times',
        fontSize: 16
      },
      normal: { //normal text. don't let name fool you!
        font: 'Times',
        fontSize: 12,
        color: "#000000",
      },
      subheader: { 
        font: 'Times',
        bold: true,
        fontSize: 12,
      },
      quote: { //italics on
        font: 'Times',
        italics: true,
        fontSize: 12
      },
      web: { //this one is specifically for hyperlinks
        font: 'Times',
        italics: true,
        color: "#1F67CD"
      },
      small: { //tiny font for bottom page
        font: 'Times',
        fontSize: 8
      }
    }
  };

  /* interpret the text data object to the desired architecture */
  let dlen =  text.length;
  var title = "";
  var header_text = "";
  var author = "";
  for(let i= 0; i < dlen; i ++){
    /* set title as file name */
    if (text[i].element.name === "Title") {
      title = text[i].text.replace(/<(.|\n)*?>/g, '');
      title = title.replace('"', '');
    }

    var mstyle = {
      style: ""
    }

    /*if (text[i].element.name === "Rights") {
      header_text = "Copyright License";  
    }*/
    
    header_text = text[i].element.name
    
    /* doc title is here. cleaned for unnecessary html -Cj */
    var docTitle = '';
    if (text[i].element.name === "Title"){
      docTitle = text[i].text.replace(/<(.|\n)*?>/g, '');
      docTitle = docTitle.replace('"', '');
      docTitle = docTitle.replace(/"/g, '');
      //if (docTitle.includes('Original')){
      //  docTitle = docTitle.replace(/\(.+?\)/gm, '');
      //}
      //else if (docTitle.includes('Cabinet')){
      //  docTitle = docTitle.replace(/\(.+?\)/gm, '')
      //}
      docTitle = docTitle.replace(/\(.+?\)/gm, '')
    }
    if (text[i].element.name === "Creator"){
      author = text[i].text
    }
    var d1 =  {
          text: header_text,
          style: 'normal'
        }

    /* maintain the description which may:
    1. contain html tags hence being removed
    2. result in a long text hence being truncated */
    
    var d2txt = text[i].text.replace(/<(.|\n)*?>/g, '');  
    d2txt = d2txt.replace(/&nbsp;/g, '');
    d2txt = d2txt.replace(/amp;/g, '');
    d2txt = d2txt.replace(/\n/g, '');
    d2txt =  truncate(d2txt, len_words);
    var d2 = {
      text: d2txt,
      style: mstyle.style,
    }
    
    /* push the text to dd object 
     * Changes meta header wording and separates title from text dump
     * to better style the cover page. -Cj
     * removes: title, relation, original format, email, publisher, date, and Copyright 
     */
    d1.text = d1.text.replace(/\bCreator\b/gm, 'Author(s)');
    d1.text = d1.text.replace(/\bSource\b/gm, 'Original Source');
    //ASK ABOUT CONSISTENCY
    dd.content.push({text: docTitle , style:'quote', margin:[0,2,0,2.5]});
    if (d1.text!== "Relation" && d1.text !== "Original Format" && d1.text !== "Email"
      && d1.text!== "Publisher" && d1.text !== "Date" && d1.text !== "Author" 
      && d1.text !== "Title" && d1.text !== 'Description'){
      dd.content.push({text: d1.text + ": " + d2.text, style:'normal', margin:[0,0,0,2.5]}) ;
    }
  }
  //dd.content.push({text: "Collection: ", style: 'normal', margin: [0,-5,0,2.5]})
  /* Adds a solid line underneath metadata. */
  dd.content.push({canvas:[{type: 'line', x1: 0, y1: 50, x2: 495, y2: 50, lineWidth: 1}], margin:[0,20,0,20]});
  /* Adds attribution to cover page. */
  dd.content.push({text: ["Suggested citation:\n ",
  author + ' "' + title + '." ',
  {text: "George Eliot Scholars", style: 'quote'},
  ", edited by Beverley Park Rilett, ",
  {text: 'http://GeorgeEliotScholars.org', link: 'http://GeorgeEliotScholars.org', style: 'web'},
  ". Accessed [Date]."]}) 
  return [dd, title];
}

/* truncate a string by the number limit of words */
function truncate(str, no_words) {
  return str.split(" ").splice(0,no_words).join(" ");
}